export default Gallery

const Section = styled.section`
    padding: 6rem 0 8rem;
    background-color: #F2DAD7;
    .heading {
        margin-bottom: 2rem;
    }
    ${responsive.md} {
        padding: 5rem 0 5rem;
        .heading {
            padding-top: 5rem;
            position: relative;
            margin: 0;
            svg {
                position: absolute;
                top: -13px;
                left: 2px;
                z-index: 2;
            }
        }
    }
    ${responsive.sm} {
        padding: 4rem 0 5rem;
        .heading {
            svg {
                width: 178px;
                top: -7px;
            }
        }
    }
`

const Card = styled.div`
    padding: 0 6%;
    height: 20vw;
    ${responsive.md} {
        padding: 0 4%;
        height: 30vw;
    }
    ${responsive.sm} {
        padding: 0 2%;
        height: 54vw;
    }
`

const Social = styled.div`
    display: flex;
    justify-content: flex-end;
    align-items: center;
    margin-top: 4rem;
    font-size: 1.8rem;
    line-height: 1;
    a {
        display: flex;
    }
    svg {
        margin-right: 0.8rem;
        path {
            transition: all .5s;
        }
    }
    a:hover svg path {
        fill: ${(props) => props.theme.colors.highlight};
        transition: all .5s;
    }
    ${responsive.md} {
        justify-content: center;
        margin-top: 2.5rem;
    }
`